<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Image Gallery</title>
  <style>
    .gallery {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
      grid-gap: 10px;
    }
    .gallery img {
      width: 150px;
      height: 150px;
      object-fit: cover; /* Ensure images cover the entire space */
      cursor: pointer;
      transition: transform 0.3s ease;
    }
    .gallery img:hover {
      transform: scale(1.05); /* Slightly increase size on hover */
    }
    .expanded-container {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: rgba(0, 0, 0, 0.8); /* Semi-transparent background */
      display: none; /* Initially hidden */
      justify-content: center;
      align-items: center;
      z-index: 9999;
    }

    .expanded {
      max-width: 80%;
      max-height: 80%;
      cursor: pointer;
    }
  </style>
</head>
<body>

<div class="gallery">
<img src="../../../img/deadhorse/deadhorse-top.JPG" alt="Image 1" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_0012.JPG" alt="Image 2" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_0016.JPG" alt="Image 3" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_0026.JPG" alt="Image 4" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6044.jpeg" alt="Image 5" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6050.jpeg" alt="Image 6" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6058.jpeg" alt="Image 7" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6061.jpeg" alt="Image 8" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6062.jpeg" alt="Image 9" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6067.jpeg" alt="Image 10" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6071.jpeg" alt="Image 11" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6078.jpeg" alt="Image 12" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6079.jpeg" alt="Image 13" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6080.jpeg" alt="Image 14" onclick="expandImage(this)">
<img src="../../../img/deadhorse/IMG_6192.HEIC" alt="Image 15" onclick="expandImage(this)">
</div>
<div class="expanded-container" onclick="closeExpanded()">
  <img class="expanded" id="expanded-image" onclick="navigateGallery(event)">
</div>

<script>
  var currentImageIndex = 0;
  var galleryImages = document.querySelectorAll('.gallery img');

  function expandImage(img) {
    currentImageIndex = Array.from(galleryImages).indexOf(img);
    document.getElementById('expanded-image').src = img.src;
    document.querySelector('.expanded-container').style.display = 'flex';
  }

  function closeExpanded() {
    document.querySelector('.expanded-container').style.display = 'none';
  }

  function navigateGallery(event) {
    // Calculate the click position relative to the expanded image
    var clickX = event.pageX - event.target.getBoundingClientRect().left;
    var imageWidth = event.target.offsetWidth;

    // Determine if the click was on the left or right side of the image
    if (clickX < imageWidth / 2) {
      // Clicked on the left side: navigate to the previous image
      currentImageIndex = (currentImageIndex - 1 + galleryImages.length) % galleryImages.length;
    } else {
      // Clicked on the right side: navigate to the next image
      currentImageIndex = (currentImageIndex + 1) % galleryImages.length;
    }

    // Update the expanded image source
    document.getElementById('expanded-image').src = galleryImages[currentImageIndex].src;
  }

  // Add event listeners to the left and right sides of the expanded image for navigation
  document.querySelector('.expanded').addEventListener('click', function(event) {
    navigateGallery(event);
  });
</script>


</body>
</html>

